package org.frameworkset.nosql.redis;

public class NodeInfo {
	private String host;
	private int port;

	public NodeInfo() {
		// TODO Auto-generated constructor stub
	}

	public String getHost() {
		return host;
	}

	public void setHost(String host) {
		this.host = host;
	}

	public int getPort() {
		return port;
	}

	public void setPort(int port) {
		this.port = port;
	}

}
